mapper.select:
select

  distinct


from wechat_pay




  order by ${orderByClause}



     limit #{mysqlOffset} , #{mysqlLength}

mapper.insert:
insert into wechat_pay (id, merchant_no, order_no, 
  has_inland_order, pay_amount, wechat_trade_no, 
  wechat_pay_status, wechat_pay_time, qunar_pay_status, 
  qunar_pay_time, insert_time, update_time
  )
values (#{id,jdbcType=CHAR}, #{merchantNo,jdbcType=CHAR}, #{orderNo,jdbcType=VARCHAR}, 
  #{hasInlandOrder,jdbcType=INTEGER}, #{payAmount,jdbcType=INTEGER}, #{wechatTradeNo,jdbcType=VARCHAR}, 
  #{wechatPayStatus,jdbcType=INTEGER}, #{wechatPayTime,jdbcType=VARCHAR}, #{qunarPayStatus,jdbcType=INTEGER}, 
  #{qunarPayTime,jdbcType=VARCHAR}, #{insertTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )
